    private void btnSalvar3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvar3ActionPerformed
        // TODO add your handling code here:

        try {
            if (validaCampos()) {
                if (preencherObj()) {
                    if (acao == 1) {
                        if (dao.insert(obj)) {
                            JOptionPane.showMessageDialog(this,
                                    "Cadastro realizado com sucesso!",
                                    "CADASTRO DE PACIENTES", 1);
                            ativaMenu(false);
                            //retornaObj(dao.selectNome(obj.getNome()));
                        } else {
                            JOptionPane.showMessageDialog(this, "Erro ao cadastrar!",
                                    "CADASTRO DE PACIENTES", 1);
                        }
                    } else if (acao == 2) {
                        if (dao.update(obj)) {
                            JOptionPane.showMessageDialog(this,
                                    "Atualizado com sucesso!",
                                    "CADASTRO DE PACIENTES", 1);
                            ativaMenu(false);
                            //retornaObj(dao.selectNome(obj.getNome()));
                        } else {
                            JOptionPane.showMessageDialog(this, "Erro ao atualizar!",
                                    "CADASTRO DE PACIENTES", 1);
                        }
                    } else {
                        JOptionPane.showMessageDialog(this,"NÃ£o reconheceu a aÃ§Ã£o!",
                                    "CADASTRO DE PACIENTES", 1);
                    }
                } else {
                    JOptionPane.showMessageDialog(this,"NÃ£o caregou o OBJ!",
                                    "CADASTRO DE PACIENTES", 1);
                }
            } else {
                JOptionPane.showMessageDialog(this,"NÃ£o validou os campos!",
                                    "CADASTRO DE PACIENTES", 1);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(this, "Erro: " + e.getMessage(), "Error", 1);
        }
    }//GEN-LAST:event_btnSalvar3ActionPerformed

